let totalSpend = 0;
let totalLeads = 0;
let sumCPM = 0;
let sumCPP = 0;
let sumCTR = 0;
let totalLinkClicks = 0; // Vari√°vel para acumular o valor de link_click
let countCPM = 0;
let countCPP = 0;
let countCTR = 0;
let totalReach = 0; // Total de reach
let totalImpressions = 0; // Total de impressions

// Fun√ß√£o para converter string de valores para float, considerando tanto ponto quanto v√≠rgula
function convertToFloat(value) {
    return parseFloat(value.replace(',', '.'));
}

// Itera sobre cada item (que representa um conjunto de dados da campanha)
items.forEach(item => {
    const jsonData = item.json;

    // Acumula o valor de spend
    if (jsonData.spend !== undefined) {
        totalSpend += convertToFloat(jsonData.spend);
    }

    // Verifica se o campo 'actions' existe e √© um array
    if (Array.isArray(jsonData.actions)) {
        jsonData.actions.forEach(action => {
            // Acumula o valor de link_click
            if (action.action_type === "link_click") {
                totalLinkClicks += parseInt(action.value, 10);
            }
            // Acumula o valor de leads (exemplo de a√ß√£o espec√≠fica)
            if (action.action_type === $item("0").$node["Dados Cliente1"].json["Action_type"]) {
                totalLeads += parseInt(action.value, 10);
            }
        });
    }

    // Acumula os valores de cpm, cpp, ctr para calcular a m√©dia
    if (jsonData.cpm !== undefined) {
        sumCPM += convertToFloat(jsonData.cpm);
        countCPM++;
    }
    if (jsonData.cpp !== undefined) {
        sumCPP += convertToFloat(jsonData.cpp);
        countCPP++;
    }
    if (jsonData.ctr !== undefined && convertToFloat(jsonData.ctr) > 0) {
        sumCTR += convertToFloat(jsonData.ctr);
        countCTR++;
    }

    // Acumula reach e impressions
    if (jsonData.reach !== undefined) {
        totalReach += convertToFloat(jsonData.reach);
    }
    if (jsonData.impressions !== undefined) {
        totalImpressions += convertToFloat(jsonData.impressions);
    }
});

// Calcula as m√©dias e a frequ√™ncia
const avgCPC = totalLinkClicks > 0 ? (totalSpend / totalLinkClicks) : 0;
const avgCPM = countCPM > 0 ? (sumCPM / countCPM) : 0;
const avgCPP = countCPP > 0 ? (sumCPP / countCPP) : 0;
const avgCTR = countCTR > 0 ? (sumCTR / countCTR) : 0;
const frequency = totalReach > 0 ? (totalImpressions / totalReach) : 0;
const cpl = totalLeads > 0 ? (totalSpend / totalLeads) : 0; // Calcula o CPL

// Retorna apenas os dados acumulados
return [
    {
        json: {
            totalSpend: totalSpend.toFixed(2).replace('.', ','),
            totalLeads: totalLeads,
            totalLinkClicks: totalLinkClicks,  // Inclui o total de link_click no resultado
            avgCPC: avgCPC.toFixed(2).replace('.', ','),
            avgCPM: avgCPM.toFixed(2).replace('.', ','),
            avgCPP: avgCPP.toFixed(2).replace('.', ','),
            avgCTR: avgCTR.toFixed(2).replace('.', ','),
            reach: totalReach,
            impressions: totalImpressions,
            frequency: frequency.toFixed(2).replace('.', ','),
            cpl: cpl.toFixed(2).replace('.', ',')
        }
    }
];

